import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

const App = () => {
    const [ip, setIp] = useState("10.10.10.10");
    const [ipN, setIpN] = useState(["0", "0", "0", "0"]);

    function handleOnChange(event) {
        setIp(event.target.value);
        let newIp = ip;
        document.title = newIp;
        let ipTyping = new Array;
        for(let i = 0; i <=3; i++) {
            ipTyping[i] = 0;
        }
        ipTyping = newIp.split(".");
        setIpN(ipTyping);
    }

    return (
      <div className="App">
        <header className="App-header">
          <div>IP Address:</div><input value={ip} onChange={handleOnChange} />
          <div style={{fontSize:14}}>Searching {ip} ... found in {ipN[0]}</div>
        </header>
        <article className="App-article">
          <div>Hello</div>
        </article>
        <footer className="App-footer">
          <div>Script by Mongkol Thamwongskul. Powered by React.</div>
        </footer>
      </div>
    )
};

export default App;
