import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

const App = () => {
    const [ip, setIp] = useState("10.10.10.10");
    const [ipN, setIpN] = useState(["0", "0", "0", "0"]);

    function handleOnChange(event) {
        setIp(event.target.value);
        ipN = ip.split(".");
    }

    return (
      <div className="App">
        <header className="App-header">
          <div>IP Address:</div><input value={ip} onChange={handleOnChange} />
          <div style={{fontSize:14}}>Searching {ip} ... found in </div>
        </header>
        <article className="App-article">
          <div>Hello</div>
        </article>
        <footer className="App-footer">
          <div>Script by Mongkol Thamwongskul. Powered by React.</div>
        </footer>
      </div>
    )
};

export default App;
